dict.string:
ObjC.import('stdlib');

function run(argv)
{
	intAuto = $.getenv('AutoExtractIcon');
	if(intAuto == 1)
	{
		Application('com.runningwithcrayons.Alfred').setConfiguration('AutoExtractIcon', 
		{
			toValue: 0,
			inWorkflow: $.getenv('alfred_workflow_bundleid'),
			exportable: true
		});
		return "false";
	}

	else
	{
		Application('com.runningwithcrayons.Alfred').setConfiguration('AutoExtractIcon', 
		{
			toValue: 1,
			inWorkflow: $.getenv('alfred_workflow_bundleid'),
			exportable: true
		});
		return "true";
	}
}
